import React, { Component, Fragment } from "react";
import './ModalBasic.css';

class BasicPopup extends Component {   

    constructor(props, context) {
        super(props, context);

        this.state = {
            modalId: this.props.modalid ? this.props.modalid : 'basic',
            modalTarget: this.props.modalid ? "#"+this.props.modalid : '#basic',
            modalLabel: this.props.modalid ? this.props.modalid+'Label' : 'basicLabel',
            showCloseBtn:  this.props.showclose ? 'btn btn-secondary' : 'btn btn-secondary d-none',
            showCloseSign:  this.props.showclosesign ? 'close' : 'close d-none',
            modalTitle: this.props.title,
            modalContent: this.props.content,
            showModal: "hide"
        };

        this.handleShowModal = this.handleShowModal.bind(this);
        this.handleHideModal = this.handleHideModal.bind(this); 

    }

    handleShowModal() {
        this.setState({showModal: 'show'})
    }

    handleHideModal() {
        this.setState({showModal: 'hide'})
    }

    render(){ 

        let classes; 
        if(!this.props.classes || this.props.classes===''){
            classes = ' modal fade';
        } else {
            classes = this.props.classes+this.state.showModal;  
        } 

        return(
            <Fragment>
            {/* <!-- Button trigger modal --> */}
            <button type="button" onClick={this.handleShowModal} className="btn btn-primary" data-toggle="modal" data-target={this.state.modalTarget}>Modal</button>

                {/* <!-- Modal --> */}
                <div className={classes} id={this.state.modalId} tabIndex="-1" role="dialog" aria-labelledby={this.state.modalLabel} aria-hidden="true">
                    <div className="modal-dialog" role="document">
                        <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id={this.state.modalLabel}>{this.state.modalTitle}</h5>
                            <button onClick={this.handleHideModal} type="button" className={this.state.showCloseSign} data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button>
                        </div>
                        <div className="modal-body">{this.state.modalContent}</div>
                        <div className="modal-footer">
                            <button onClick={this.handleHideModal} type="button" className={this.state.showCloseBtn} data-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-primary">Save</button>
                        </div>
                        </div>
                    </div>
                </div>
            </Fragment>
        );
    }
}
export default BasicPopup;
