// Action for Increment Counter
export function increment() {
    return {
        type: 'INCREMENT'
    }
}

// Action for Decrement Counter
export function decrement() {
    return {
        type: 'DECREMENT'
    }
}

// Action for Reset Counter
export function reset() {
    return {
        type: 'RESET'
    }
}
