import React, { Component, Fragment } from "react";
import './Newsletter.css';

import Image from '../../images/Images';   

class Newsletter extends Component {   

    render(){

        let classes; 
        if(!this.props.data.classes || this.props.data.classes===''){
            classes = 'card mb-3';
        } else {
            classes = this.props.data.classes; 
        } 

        return(
            <Fragment>
                {/* <!-- Card Start --> */}
                <div className="card shadow1 rounded-0 mb-3">
                <div className="card-header bg-white border-0 shadow2 text-uppercase">{this.props.data.title}</div>
                <div className="card-body pb-0">
                    <p>{this.props.data.text}</p>
                    <form className="p-0 m-0">
                    <div className="form-row">
                        <div className="col-12 col-md-8">
                        <div className="form-group mb-4">
                            <input className="form-control input_design" type="email" placeholder="Enter email address." />
                        </div>
                        </div>
                        <div className="col-12 col-md-4">
                        <input className="btn btn-primary w-100" type="button" name="submit" value="Submit" />
                        </div>
                    </div>
                    </form>
                </div>
                </div>
                {/* <!-- Card End --> */}
            </Fragment>
        );
    }
}
export default Newsletter;
