import React, { Component, Fragment } from "react";
import './ModalBasic.css';

class BasicPopup extends Component {   

    render(){ 

        let classes; 
        if(!this.props.classes || this.props.classes===''){
            classes = ' modal fade';
        } else {
            classes = ' modal fade '+this.props.classes;  
        }  

        let modalid = this.props.modalid ? this.props.modalid : 'basic';
        let modaltarget = this.props.modalid ? "#"+this.props.modalid : '#basic';
        let modallabel = this.props.modalid ? this.props.modalid : 'basicModalLabel';
        // let modalid =  "#"+this.props.modalid;
        // let modalid = this.props.modalid;

        return(
            <Fragment>
            {/* <!-- Button trigger modal --> */}
            <button type="button" className="btn btn-primary" data-toggle="modal" data-target={modaltarget}>Modal Button</button>

                {/* <!-- Modal --> */}
                <div className={classes} id={modalid} tabIndex="-1" role="dialog" aria-labelledby={modallabel} aria-hidden="true">
                    <div className="modal-dialog" role="document">
                        <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id={modallabel}>Modal title</h5>
                            <button type="button" className="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button>
                        </div>
                        <div className="modal-body">Modal Content</div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-primary">Save</button>
                        </div>
                        </div>
                    </div>
                </div>
            </Fragment>
        );
    }
}
export default BasicPopup;
