import React, { Component, Fragment } from "react";
import '../Card.css';

import Image from '../../images/Images';   

class Testimonial extends Component {   
    render(){
        return(
            <Fragment>
                <div className={this.props.data.classes}>
                    <div className="mx-auto text-center py-3">
                    <Image img={this.props.img}/>
                        <div className="card-icon position-absolute">
                            <i className="fas fa-quote-left"></i>
                        </div>
                    </div>
                    <div className="card-body text-center">
                        <div className="card-text mb-3">{this.props.data.text}</div>
                        <div className="card-title font-weight-bold">{this.props.data.person}</div>
                        <div className="text-secondary"><small>{this.props.data.company}</small></div>
                    </div>
                </div>
            </Fragment>
        );
    }
}
export default Testimonial;
