import React, { Component, Fragment } from "react";
import './Card.css';

import Image from '../images/Images';

import TestimonialCard from './testimonial/Testimonial';

class Card extends Component {

    state = { 
        data: {   
            person: 'Shrikant Yadav 2',
            company: 'Company Name',
            message: 'This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.',
            classes: 'card mb-3 pt-3 testimonials_card2'
        }
    }  

    render(){

        let card;
        if (this.state.cardtype==='testimonial') {
            card = <TestimonialCard data={this.state.data} />
        } else {
            card = <div className="brand-name">{this.props.brandname}</div>
        }

        return(
            <Fragment>

                 {card}

            </Fragment>
        );
    }
}
export default Card;
