import React, { Component } from 'react';

class Counter extends Component {
    state = {
        count: 1
    }

    render() {
        return (
            <div>
                <span className="badge badge-primary p-3">{this.formatCount()}</span>
                <button className="btn btn-primary px-3 btn-sm">Increment</button>
            </div>
            );
    }

    formatCount() {
        const { count } = this.state;
        return count === 0 ? "Zero" : count;
    }
}



export default Counter;
