import React, { Component, Fragment } from "react";
import './ModalBasic.css';

class BasicPopup extends Component {   

    constructor(props, context) {
        super(props, context);

        this.handleShow = this.handleShow.bind(this);
        this.handleClose = this.handleClose.bind(this);

        this.state = {
            modalId: this.props.modalid ? this.props.modalid : 'basic',
            modalTarget: this.props.modalid ? "#"+this.props.modalid : '#basic',
            modalLabel: this.props.modalid ? this.props.modalid+'Label' : 'basicLabel',
            showCloseBtn:  this.props.showclose ? '<button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>' : '',
            hideCloseSign:  this.props.hideclosesign ? '' : '<button type="button" className="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button>',
            modalTitle: this.props.title,
            modalContent: this.props.content
        };
      }

    render(){ 

        let classes; 
        if(!this.props.classes || this.props.classes===''){
            classes = ' modal fade';
        } else {
            classes = this.props.classes;  
        }  

        return(
            <Fragment>
            {/* <!-- Button trigger modal --> */}
            <button type="button" className="btn btn-primary" data-toggle="modal" data-target={this.state.modalTarget}>Modal</button>

                {/* <!-- Modal --> */}
                <div className={classes} id={this.state.modalId} tabIndex="-1" role="dialog" aria-labelledby={modalLabel} aria-hidden="true">
                    <div className="modal-dialog" role="document">
                        <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id={this.state.modalLabel}>{this.state.modalTitle}</h5>
                            {hideCloseSign}
                        </div>
                        <div className="modal-body">{modalContent}</div>
                        <div className="modal-footer">
                            {showCloseBtn}
                            <button type="button" className="btn btn-primary">Save</button>
                        </div>
                        </div>
                    </div>
                </div>
            </Fragment>
        );
    }
}
export default BasicPopup;
