import React, { Component, Fragment} from "react";
import './Blog.css';

// import Card from '../../card/Card'; 
import Heading from '../../heading/Heading'; 
import HorizontalCard from '../../card/blog-horizontal/HorizontalCard'; 

class Blog extends Component {


	state = { 
        blogdata: [
            {
                id: '1',
                title: 'Card Title',
                imgsrc: 'https://picsum.photos/id/891/300/200',
                imgalt: 'Card Image',
                showimg: true,
                text: 'This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.',
            },
            {
                id: '2',
                title: 'Company Name',
                imgsrc: 'https://picsum.photos/id/891/300/200',
                imgalt: 'Shrikant Image',
                showimg: true,
                text: 'This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.',
            }

        ]
    }  

	render() {
		return(
		    <Fragment>
			<Heading element='h1' classes="bg-secondary text-white" text="Blog" />		
			<section id='section1' className="section_common_80_60">  
				<div className="container">
					<div className="w_900 mx-auto">
					<div className="row">
						<div className="col-12">
							<HorizontalCard data={this.state.blogdata} />
						</div>
					</div>
					</div>
				</div>
			</section> 
			</Fragment>
		);
	}
} 

export default Blog;
